PosteriorMean_acc <- function(value)
{
	if (!isnull(.self$.cobj)) {
		if (missing(value)) {
			so_Bayesian_get_PosteriorMean(.self$.cobj)
		} else {
			if (!is(value, "data.frame")) {
				stop("object must be of type 'data.frame'")
			}
			so_Bayesian_set_PosteriorMean(.self$.cobj, value)
		}
	}
}

