so_Estimates = setRefClass("so_Estimates",
	fields=list(
		Mean = Mean_acc,
		Median = Median_acc,
		Mode = Mode_acc,
		Samples = Samples_acc,
		.cobj = "externalptr"
	),
	methods=list(
		copy = function() {
			copy = so_Estimates_copy(.self$.cobj)
			so_Estimates$new(cobj=copy)
		},
		initialize = function(cobj) {
			if (missing(cobj)) {
				.cobj <<- so_Estimates_new()
			} else {
				.cobj <<- cobj
			}
		},
		finalize = function() {
			so_Estimates_unref(.self$.cobj)
		}
	)
