AIC_acc <- function(value)
{
	if (!isnull(.self$.cobj)) {
		if (missing(value)) {
			so_InformationCriteria_get_AIC(.self$.cobj)
		} else {
			if (!is(value, "numeric")) {
				stop("object must be of type 'numeric'")
			}
			so_InformationCriteria_set_AIC(.self$.cobj, value)
		}
	}
}

