{
	so_OptimalDesign *obj = so_OptimalDesign_copy(R_ExternalPtrAddr(self));
	if (!obj) {
		error("Failed to copy so_OptimalDesign object");
	}
	return R_MakeExternalPtr(obj, R_NilValue, R_NilValue);
}

